import { useState } from "react";
import { SidebarLateral } from "./SidebarLateral";
import { Input, Textarea, DivNotas, Button, DivButton } from "./styles";

export const Notas = (secoes) => {
    const [titulo, setTitulo] = useState();

    function handleOnChange(e) {
        setTitulo(e.target.value);
    }

    const Componente = (titulo) => {
        return <SidebarLateral novaNota={titulo} />
    };

    return (
        <>
            <SidebarLateral />
            <DivNotas>
                <h1>Cadastrar nota</h1>
                <Input type="text" name="titulo" placeholder="Titulo" onChange={handleOnChange} />
                <Textarea name="titulo" placeholder="Digite suas anotações" />
                <DivButton>
                    <Button onClick={(titulo) => Componente(titulo)}>Cadastrar</Button>
                </DivButton>
            </DivNotas >
        </>
    );
}
