import { Link } from 'react-router-dom';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { useState } from 'react';

export const SidebarLateral = ({ novaNota }) => {
    const [secaonotas, setSecaoNotas] = useState([
        {
            secao: 'Seção 1',
            notas: ['Nota 1', 'Nota 2']
        },
        {
            secao: 'Seção 2',
            notas: ['Nota 3', 'Nota 4']
        },
    ]);
    console.log(novaNota);
    const [notas, setNotas] = useState(novaNota);

    return (
        <Sidebar>
            <Menu>
                <MenuItem><Link to="/">Home</Link></MenuItem>
                <MenuItem>Notas</MenuItem>
                {secaonotas.map((secaonota) => {
                    return (
                        <SubMenu key={secaonota.secao} label={secaonota.secao}>
                            {secaonota.notas.map((nota) => {
                                return (
                                    <MenuItem key={nota}>{nota}</MenuItem>
                                );
                            })}
                            <MenuItem><Link to="/notas">+ Nova nota</Link></MenuItem>
                        </SubMenu>
                    );
                })}
                <MenuItem><Link to="/secao">+ Nova Seção</Link></MenuItem>
            </Menu>
        </Sidebar >
    );
}
