import { Link } from 'react-router-dom';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import { useState } from 'react';

export const SidebarLateral = () => {
    const [secaonotas, setSecaoNotas] = useState(
        {
            secao: 'Seção 1',
            notas: ['Nota 1', 'Nota 2']
        },
        {
            secao: 'Seção 2',
            notas: ['Nota 3', 'Nota 4']
        },
    );

    return (
        <Sidebar>
            <Menu
                menuItemStyles={{
                    button: {
                        [`&.active`]: {
                            backgroundColor: '#13395e',
                            color: '#b6c8d9',
                        },
                    },
                }}
            >
                <SubMenu label="Seção 1">
                    <MenuItem> Nota 1 </MenuItem>
                    <MenuItem> Nota 2 </MenuItem>
                </SubMenu>
                <SubMenu label="Seção 2">
                    <MenuItem> Nota 1 </MenuItem>
                    <MenuItem> Nota 2 </MenuItem>
                </SubMenu>
                {secaonotas.map((secaonota) => {
                    return (
                        <SubMenu key={secaonota.secao} label={secaonota.secao}>
                            {secaonota.notas.map((nota) => {
                                return (
                                    <MenuItem key={nota}>{nota}</MenuItem>
                                );
                            })}
                        </SubMenu>
                    );
                })}
                <MenuItem> + Nova Seção </MenuItem>
            </Menu>
        </Sidebar >
    );
}
