import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css'; // Importe o arquivo de estilos da biblioteca

export const SidebarLateral = ({ novaNota }) => {
    const [secaonotas, setSecaoNotas] = useState([
        {
            id: '1',
            secao: 'Seção 1',
            notas: ['Nota 1', 'Nota 2', 'Nota 3', 'Nota 4', 'Nota 5', 'Nota 6']
        },
        {
            id: '2',
            secao: 'Seção 2',
            notas: ['Nota 1', 'Nota 2', 'Nota 3', 'Nota 4', 'Nota 5', 'Nota 6', , 'Nota 7', 'Nota 8']
        },
    ]);

    const [kanban, setKanban] = useState(['Kanban 1', 'Kanban 2']);

    return (
        <Sidebar className="custom-menu"> {/* Aplicando a classe custom-menu */}
            <Menu>
                <MenuItem>
                    <Link to="/" className="custom-menu">Home</Link> {/* Aplicando a classe custom-menu */}
                </MenuItem>
                <SubMenu label="Notas" className="custom-menu"> {/* Aplicando a classe custom-menu */}
                    {secaonotas.map((secaonota) => {
                        return (
                            <MenuItem key={secaonota.secao} className="custom-menu"> {/* Aplicando a classe custom-menu */}
                                <Link to={"/listarnotas/" + secaonota.id} className="custom-menu"> {secaonota.secao} </Link> {/* Aplicando a classe custom-menu */}
                            </MenuItem>
                        );
                    })}
                    <MenuItem className="custom-menu"> {/* Aplicando a classe custom-menu */}
                        <Link to="/secao" className="custom-menu">+ Nova Seção</Link> {/* Aplicando a classe custom-menu */}
                    </MenuItem>
                </SubMenu>
                <SubMenu label="Kanban" className="custom-menu"> {/* Aplicando a classe custom-menu */}
                    {kanban.map((kanban) => {
                        return (
                            <MenuItem key={kanban} className="custom-menu"> {/* Aplicando a classe custom-menu */}
                                {kanban}
                            </MenuItem>
                        );
                    })}
                    <MenuItem className="custom-menu"> {/* Aplicando a classe custom-menu */}
                        <Link to="/novokanban" className="custom-menu">+ Novo Kanban</Link> {/* Aplicando a classe custom-menu */}
                    </MenuItem>
                </SubMenu>
            </Menu>
        </Sidebar>
    );
}
