import React from 'react';
import { SidebarRoutes } from './SidebarRoutes';
import { DivGeral } from './components/styles';

// Cabeçalho básico
const Header = () => {
    const headerStyle = {
        backgroundColor: '#1c2424',
        height: '60px',
        width: '100%', // Adicione esta linha para ocupar toda a largura
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'white',
        fontWeight: 'bold',
    };

    return (
        <div style={headerStyle}>
            Meu Cabeçalho
        </div>
    );
};

function App() {
    const appStyle = {
        display: 'flex',
        flexDirection: 'column', // Coloque o conteúdo em coluna
        height: '100vh',
    };

    return (
        <div style={appStyle}>
            {/* Adicione o cabeçalho aqui */}
            <Header />
            <SidebarRoutes />
        </div>
    );
}

export default App;
