import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { Sidebar, Menu, MenuItem, SubMenu } from 'react-pro-sidebar';

export const SidebarLateral = ({ novaNota }) => {
    const [secaonotas, setSecaoNotas] = useState([
        {
            id: '1',
            secao: 'Seção 1',
            notas: ['Nota 1', 'Nota 2', 'Nota 3', 'Nota 4', 'Nota 5', 'Nota 6']
        },
        {
            id: '2',
            secao: 'Seção 2',
            notas: ['Nota 1', 'Nota 2', 'Nota 3', 'Nota 4', 'Nota 5', 'Nota 6', 'Nota 7', 'Nota 8']
        },
    ]);

    const [kanban, setKanban] = useState(['Kanban 1', 'Kanban 2']);
    const menuRef = useRef(null);

    const handleMenuItemHover = () => {
        if (menuRef.current) {
            menuRef.current.style.color = 'white';
        }
    };

    const handleMenuItemLeave = () => {
        if (menuRef.current) {
            menuRef.current.style.color = 'initial'; // Retorna à cor padrão
        }
    };

    useEffect(() => {
        if (menuRef.current) {
            menuRef.current.addEventListener('mouseenter', handleMenuItemHover);
            menuRef.current.addEventListener('mouseleave', handleMenuItemLeave);
        }

        return () => {
            if (menuRef.current) {
                menuRef.current.removeEventListener('mouseenter', handleMenuItemHover);
                menuRef.current.removeEventListener('mouseleave', handleMenuItemLeave);
            }
        };
    }, []);

    return (
        <Sidebar>
            <Menu style={{ backgroundColor: "#1c2424" }}>
                <MenuItem ref={menuRef}><Link to="/" style={{ color: 'white' }}>Home</Link></MenuItem>
                <SubMenu label="Notas" style={{ backgroundColor: "#1c2424" }}>
                    {secaonotas.map((secaonota) => {
                        return (
                            <MenuItem key={secaonota.secao} style={{ backgroundColor: "#1c2424" }}>
                                <Link to={"/listarnotas/" + secaonota.id} style={{ color: 'white' }}>{secaonota.secao}</Link>
                            </MenuItem>
                        );
                    })}
                    <MenuItem style={{ backgroundColor: "#1c2424" }}><Link to="/secao" style={{ color: 'white' }}>+ Nova Seção</Link></MenuItem>
                </SubMenu>
                <SubMenu label="Kanban" style={{ backgroundColor: "#1c2424" }}>
                    {kanban.map((kanban) => {
                        return (
                            <MenuItem key={kanban} style={{ backgroundColor: "#1c2424" }}>
                                <span style={{ color: 'white' }}>{kanban}</span>
                            </MenuItem>
                        );
                    })}
                    <MenuItem style={{ backgroundColor: "#1c2424" }}><Link style={{ color: 'white' }}>+ Novo Kanban</Link></MenuItem>
                </SubMenu>
            </Menu>
        </Sidebar>
    );
}
